package com.hsicen.parallaxviewimport android.view.ViewGroupinterface ParallaxViewAdapter {  fun onCreateViewHolder(parent: ViewGroup, viewType: Int): ParallaxViewHolder  fun onBindViewHolder(holder: ParallaxViewHolder, position: Int, data: ParallaxData)  fun onAttachedToWindow(holder: ParallaxViewHolder, position: Int, data: ParallaxData) {}  fun onDetachedFromWindow(holder: ParallaxViewHolder, position: Int, data: ParallaxData) {}}