package com.hsicen.parallaxviewimport androidx.annotation.Keepimport androidx.appcompat.widget.LinearLayoutCompatimport androidx.appcompat.widget.LinearLayoutCompat.OrientationModeimport java.io.Serializable@Keepclass ParallaxViewConfig(    var offsetFirst: Int = 0,    var offsetSecond: Int = 0,    var itemMargin: Int = 0,    var autoLoop: Boolean = true,    var direction: ParallaxDirection = ParallaxDirection.Forward,    var reverse: Boolean = true,    @OrientationMode var orientation: Int = LinearLayoutCompat.HORIZONTAL,    var parallaxAdapter: ParallaxViewAdapter) : Serializable